<?php//Author  : Synapseindianamespace Synapseindia\Abandonedcart\Setup;  use Magento\Framework\Setup\InstallSchemaInterface;use Magento\Framework\Setup\ModuleContextInterface;use Magento\Framework\Setup\SchemaSetupInterface;use Magento\Framework\Setup\UpgradeSchemaInterface;use Magento\Framework\DB\Ddl\Table;/** * Class InstallSchema * @package Mageplaza\SocialLogin\Setup */    /**     * install tables     *     * @param \Magento\Framework\Setup\SchemaSetupInterface   $setup     * @param \Magento\Framework\Setup\ModuleContextInterface $context     * @return void     * @SuppressWarnings(PHPMD.ExcessiveMethodLength)     */    class UpgradeSchema implements UpgradeSchemaInterface     {	public function upgrade( SchemaSetupInterface $setup, ModuleContextInterface $context ) {        $installer = $setup;        $installer->startSetup();        if (!$installer->tableExists('abandonedcart_listcrontable')) {            $table = $installer->getConnection()->newTable(                $installer->getTable('abandonedcart_listcrontable'))->addColumn('id', \Magento\Framework\DB\Ddl\Table::TYPE_INTEGER, 11,                    [                        'identity' => true,                        'nullable' => false,                        'primary'  => true,                        'unsigned' => true,                    ],                    'ID'                )                ->addColumn(                    'email', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'email')->addColumn(                    'product', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'product')->addColumn(                    'quantity', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'quantity'                )->addColumn(                    'total_cart', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'total_cart'                )->addColumn(                    'is_email_send', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'os_email_send'                )->addColumn(                    'last_email_sent_on', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'last_email_sent_on'                )->addColumn(                    'next_sequence_on', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'next_sequence_on'                )->addColumn(                    'email_content', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['unsigned' => true, 'nullable => true'], 'email_content'                )->addColumn(                    'ip', \Magento\Framework\DB\Ddl\Table::TYPE_TEXT, 500, ['default' => ''], 'ip'                );            $installer->getConnection()->createTable($table);        }        $installer->endSetup();    }}